mapper.select:
	select * from (
	select rownum rnum, data.* from (
    	select num, name, birth, symptom, to_char(created, 'YYYY-MM-DD') created
    	from hospital
    	where ${searchKey} like '%' || #{searchValue} || '%'
    	order by num desc
    	) data
	)

	where rnum >= #{start} and rnum <= #{end}
